import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';

export default class Box extends Component {
    static contextType = AppContext;
    state = {
        isCategory: false,
    };

    render() {
        const { isCategory, category, value, question } = this.props;
        if (isCategory) {
            return <li className="category">{this.props.category}</li>;
        } else {
            return <li className="clue">{value}</li>;
        }
    }
}
