import React, { useContext, useEffect, useState } from 'react';
import AppContext from '../providers/appContext';
import { withRouter } from 'react-router-dom';

const Box = (props) => {
    const context = useContext(GlobalState);
    const [clue, setClue] = useState();
    const { isCategory, category, value } = this.props;
    const { selected } = clue;

    useEffect(() => {
        const { question, id, answer, title, selected } = props;
        const state = {
            question: question,
            answer: answer,
            title: title,
            id: id,
            selected: selected,
        };

        setClue(state);
    }, []);

    const handleClick = (e) => {
        const { question, id, title } = clue;
        context.setAnsweredClues(context.answeredClues.concat(id));
        this.props.history.push({
            pathname: `/clue/${id}`,
            state: { question: question, clue: title },
        });
    };

    if (isCategory) {
        return <li className="category">{category}</li>;
    } else {
        return (
            <li className="clue" onClick={handleClick}>
                {selected ? '' : value}
            </li>
        );
    }
};

export default withRouter(Box);
