import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';
import axios from 'axios';

export default class Box extends Component {
    static contextType = AppContext;
    state = {
        isCategory: false,
    };

    componentDidMount() {
        const { id } = this.props;
        if (id) {
            axios(`http://jservice.io/api/clues?category=${id}`).then(
                (response) => {
                    this.setState({
                        clues: response.data,
                    });
                }
            );
        }
    }

    renderBox() {
        const { isCategory } = this.props;
        const context = this.context;
        const { clues } = this.state;
        console.log(clues);

        if (isCategory) {
            return <li>{this.props.category}</li>;
        }
        if (clues) {
            return clues.map((key, index) => {
                const { value, question } = key;
                if (index < context.clueCount) return <li>{question}</li>;
            });
        }
        return <li></li>;
    }

    render() {
        return this.renderBox();
    }
}
