import React, { Component } from 'react';
import axios from 'axios';
import { AppContext } from '../providers/AppContext';

export default class Box extends Component {
    static contextType = AppContext;
    state = {
        isCategory: false,
    };

    componentDidMount() {
        const { id } = this.props;
        const context = this.context;
        let count = context.offset;

        if (!this.state.isCategory) {
            axios(`http://jservice.io/api/clues?category=${id}`).then(
                (response) => {
                    console.log(response.data);
                    this.setState({
                        clue: response.data,
                    });
                }
            );
        }
        count = count + 1;
        context.setOffset(count);
    }

    renderBox() {
        const { isCategory, id } = this.props;
        const { clue } = this.state;
        const context = this.context;
        if (isCategory) {
            return <li>{this.props.category}</li>;
        } else {
            return Array(context.clueCount).fill(<li>$</li>);
        }
    }

    render() {
        return this.renderBox();
    }
}
