import React, { Component } from 'react';
import axios from 'axios';
import { AppContext } from '../providers/AppContext';

export default class Box extends Component {
    static contextType = AppContext;
    state = {
        isCategory: false,
    };

    componentDidMount() {
        const { id } = this.props;
        const context = this.context;
        let count = context.offset;

        if (!this.state.isCategory) {
            axios(
                `http://jservice.io/api/clues?category=${id}&offset=${context.offset}`
            ).then((response) => {
                const { value } = response.data;
                console.log(response.data);
                this.setState({
                    value: value,
                });
            });
        }
        count = count + 1;
        context.setOffset(count);
    }

    renderBox() {
        const { isCategory, id } = this.props;
        const { value } = this.state;
        if (isCategory) {
            return <li>{this.props.category}</li>;
        } else {
            return <li>{value}</li>;
        }
    }

    render() {
        return this.renderBox();
    }
}
