import React, { useContext, useEffect, useState } from 'react';
import AppContext from '../context/appContext';
import axios from 'axios';
import Box from '../components/Box';
import { withRouter } from 'react-router-dom';

const Board = (props) => {
    const { state, dispatch } = useContext(AppContext);
    const { categories, clues, clueCount, dailyDouble } = state;
    const [boardState, setBoardState] = useState({
        categories: [],
        clues: [],
        isLoading: true,
        offset: 0,
    });

    useEffect(() => {
        console.log('adasdsds');
        if (state.categories.length > 0 && state.clues.length > 0) {
        } else {
            fetchData();
        }
    }, []);

    const fetchData = async () => {
        // const dailyDouble = Math.floor(Math.random() * state.clueCount);
        // state.dailyDouble(dailyDouble);

        try {
            axios('http://jservice.io/api/categories?count=100').then(
                async (response) => {
                    await response.data.map((item, index) => {
                        const { clues_count, id } = item;
                        if (
                            clues_count >= props.location.state.clueCount &&
                            index < props.location.state.categoryCount
                        ) {
                            dispatch({
                                type: 'setCategories',
                                payload: item,
                            });
                            axios(
                                `http://jservice.io/api/clues?category=${id}`
                            ).then((response) => {
                                dispatch({
                                    type: 'setClues',
                                    payload: response.data,
                                });

                                setBoardState({ isLoading: false });
                            });
                        }
                    });
                }
            );
        } catch (error) {
            console.log(error);
        }
    };

    const resetBoard = () => {
        let reset = window.confirm('Are you sure you want to restart?');
        if (reset) {
            this.props.history.push({ pathname: '/' });
        }
    };

    //const { categories, clueCount, clues } = state;

    return (
        <div className="Board">
            {boardState.isLoading ? (
                <h2>Creating...</h2>
            ) : (
                <React.Fragment>
                    {categories.map((item, index) => {
                        const { title } = item;

                        const clueArray = clues[index];
                        return (
                            <ul key={`col-${index}`} className="list">
                                <Box
                                    category={title}
                                    className="category"
                                    isCategory={true}
                                />
                                {clueArray.map((key, index) => {
                                    const {
                                        value,
                                        question,
                                        answer,
                                        category,
                                        id,
                                    } = key;
                                    const { title } = category;
                                    const selected = state.answeredClues.includes(
                                        id
                                    );

                                    if (index < clueCount) {
                                        return (
                                            <Box
                                                id={id}
                                                value={value}
                                                question={question}
                                                answer={answer}
                                                title={title}
                                                selected={selected}
                                            />
                                        );
                                    } else {
                                        return null;
                                    }
                                })}
                            </ul>
                        );
                    })}
                    <button
                        onClick={() => {
                            if (
                                window.confirm(
                                    'Are you sure you want to restart?'
                                )
                            ) {
                                this.props.history.push('/');
                                state.setLoading(true);
                            }
                        }}
                    >
                        reset
                    </button>
                </React.Fragment>
            )}
        </div>
    );
};

export default Board;
