import React, { Component, useContext, useEffect, useState } from 'react';
import AppContext from '../context/appContext';

const Clue = (props) => {
    const { state, dispatch } = useContext(AppContext);
    const [clueState, setClueState] = useState({
        question: '',
        answer: '',
        isLoading: true,
        showClue: true,
    });

    useEffect(() => {
        const { question, answer, clue } = this.props.location.state;
        this.setClueState({
            question: question,
            answer: answer,
            isLoading: false,
            clue: clue,
        });
    }, []);

    const toggleClue = () => {
        this.setClueState({ showClue: !this.state.showClue });
    };

    const handleClick = () => {
        this.props.history.goBack();
    };

    const { question, showClue, clue } = this.clueState;
    return (
        <div>
            {!showClue ? <p>{clue}</p> : <p>{question}</p>}
            {!showClue ? (
                <button onClick={this.toggleClue}>Show Question</button>
            ) : (
                <button onClick={this.toggleClue}>Show Clue</button>
            )}
            <button onClick={this.handleClick}>Done</button>
        </div>
    );
};

export default Clue;
