import React, { Component, useContext, useEffect, useState } from 'react';
import { AppContext } from '../providers/AppContext';
import axios from 'axios';

export default function Board() {
    const board = useContext(AppContext);
    const [validBoard, setValidBoard] = useState(false);

    const init = async () => {};

    useEffect(() => {
        axios('http://jservice.io/api/categories?count=100').then(
            async (response) => {
                const t = response.data.map(async (item) => {
                    const { clues_count } = item;
                    if (clues_count >= board.categoryCount) {
                        return item;
                    }
                });
                console.log(t);
                board.setCategories(t);
            }
        );
    }, []);

    return <div>this is a board</div>;
}
