import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';
import axios from 'axios';
import Box from '../components/Box';

export default class Board extends Component {
    static contextType = AppContext;
    state = {
        categories: [],
        clues: [],
        isLoading: true,
        offset: 0,
    };
    componentDidMount() {
        let value = this.context;
        try {
            axios('http://jservice.io/api/categories?count=100').then(
                async (response) => {
                    await response.data.map((item) => {
                        const { clues_count, id } = item;
                        if (
                            clues_count >= value.clueCount &&
                            this.state.categories.length < value.categoryCount
                        ) {
                            this.setState({
                                categories: this.state.categories.concat(item),
                            });

                            axios(
                                `http://jservice.io/api/clues?category=${id}`
                            ).then((response) => {
                                this.setState({
                                    clues: this.state.clues.concat([
                                        response.data,
                                    ]),
                                });
                                console.log(this.state.clues.length);
                                if (
                                    this.state.clues.length ===
                                    value.categoryCount
                                ) {
                                    this.setState({
                                        isLoading: false,
                                        clueCount: value.clueCount,
                                    });
                                }
                            });
                        }
                    });
                }
            );
        } catch (error) {
            console.log(error);
        }
    }

    componentDidUpdate() {
        const context = this.context;
    }

    render() {
        const { categories, clues, offset, clueCount } = this.state;

        return (
            <div className="Board">
                {this.state.isLoading ? (
                    <h2>Creating...</h2>
                ) : (
                    <React.Fragment>
                        <table>
                            {categories.map((item, index) => {
                                const { title, id } = item;
                                if (clues.length > 0) {
                                    const clueArray = clues[index];
                                    return (
                                        <>
                                            <tr>
                                                <Box
                                                    category={title}
                                                    className="category"
                                                    isCategory={true}
                                                />
                                            </tr>
                                            <tr>
                                                {clueArray.map((key, index) => {
                                                    const {
                                                        category_id,
                                                        value,
                                                        question,
                                                    } = key;

                                                    if (index < clueCount) {
                                                        return (
                                                            <Box
                                                                category={
                                                                    category_id
                                                                }
                                                                value={value}
                                                                question={
                                                                    question
                                                                }
                                                            />
                                                        );
                                                    } else {
                                                        return null;
                                                    }
                                                })}
                                            </tr>
                                        </>
                                    );
                                } else {
                                    return null;
                                }
                            })}
                        </table>
                    </React.Fragment>
                )}
            </div>
        );
    }
}
