import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';
import axios from 'axios';
import Box from '../components/Box';

export default class Board extends Component {
    static contextType = AppContext;
    state = {
        categories: [],
        clues: [],
        isLoading: true,
        offset: 0,
    };
    componentDidMount() {
        let value = this.context;
        try {
            axios('http://jservice.io/api/categories?count=100').then(
                async (response) => {
                    await response.data.map((item) => {
                        const { clues_count } = item;
                        if (
                            clues_count >= value.categoryCount &&
                            this.state.categories.length < value.categoryCount
                        ) {
                            this.setState({
                                categories: this.state.categories.concat(item),
                            });
                        }
                    });
                    this.setState({
                        isLoading: false,
                        clues: Array(value.clueCount),
                    });
                }
            );
        } catch (error) {
            console.log(error);
        }
    }

    render() {
        const { categories, clues, offset } = this.state;
        const context = this.context;

        return (
            <div className="Board">
                {this.state.isLoading ? (
                    <h2>Loading...</h2>
                ) : (
                    <React.Fragment>
                        {categories.map((item, index) => {
                            const { title, id } = item;

                            return (
                                <ul key={`col-${index}`}>
                                    <Box
                                        className="category"
                                        category={title}
                                        className="category"
                                        isCategory={true}
                                    />
                                    <Box className="clue" id={id} />
                                </ul>
                            );
                        })}
                    </React.Fragment>
                )}
            </div>
        );
    }
}
