import React, { useContext, useState } from 'react';
import { useHistory } from 'react-router-dom';
import '../App.scss';
import { AppContext } from '../providers/AppContext';

function Home() {
    const board = useContext(AppContext);
    let history = useHistory();

    const handleSubmit = () => {
        if (board.categoryCount > 0 && board.clueCount > 0) {
            board.setInitialState(board);
            history.push('/board');
        }
    };
    const handleInputChange = (e) => {
        const input = e.target.name;

        switch (input) {
            case 'categories':
                board.setCategoryCount(parseInt(e.target.value));
                break;
            case 'clues':
                board.setClueCount(parseInt(e.target.value));
                break;
            default:
                break;
        }
    };
    return (
        <div className="Home">
            <h2>Jeopardy!</h2>
            <input
                type="number"
                placeholder="Number of Categories"
                onChange={handleInputChange}
                name="categories"
            />
            <input
                type="number"
                placeholder="Number of Clues"
                onChange={handleInputChange}
                name="clues"
            />
            <button onClick={handleSubmit}>Create</button>
        </div>
    );
}

export default Home;
