import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';
import { withRouter } from 'react-router-dom';

class Box extends Component {
    static contextType = AppContext;
    state = {
        isCategory: false,
    };

    componentDidMount() {
        const { question, id, answer } = this.props;
        this.setState({
            question: question,
            answer: answer,
        });
    }

    handleClick = (e) => {
        this.props.history.push({
            pathname: '/clue',
            state: { question: this.state.question, answer: this.state.answer },
        });
    };

    render() {
        const { isCategory, category, value } = this.props;
        if (isCategory) {
            return <li className="category">{category}</li>;
        } else {
            return (
                <li className="clue" onClick={this.handleClick}>
                    {value}
                </li>
            );
        }
    }
}

export default withRouter(Box);
