import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';
import axios from 'axios';

export default class Board extends Component {
    static contextType = AppContext;
    state = {
        categories: [],
        isLoading: false,
    };
    componentDidMount() {
        let value = this.context;
        try {
            axios('http://jservice.io/api/categories?count=100').then(
                async (response) => {
                    await response.data.map((item) => {
                        const { clues_count } = item;
                        if (clues_count >= value.categoryCount) {
                            this.setState({
                                categories: this.state.categories.concat(item),
                            });
                        }
                    });
                }
            );
        } catch (error) {
            console.log(error);
        }
    }

    render() {
        const { categoryCount } = contextType;
        const loading = this.state.isLoading;
        const { categories } = this.state;
        return (
            <div className="Board">
                {loading ? (
                    <h2>Loading...</h2>
                ) : (
                    <React.Fragment>
                        {categories.map((item, index) => {
                            const { title } = item;
                            if (index <= categoryCount) return <p>{title}</p>;
                        })}
                    </React.Fragment>
                )}
            </div>
        );
    }
}
