    getClue() {
        axios(`http://jservice.io/api/clues?category=${id}`).then(
            (response) => {
                if (this.state.clues.length <= value.clueCount) {
                    this.setState({
                        clues: this.state.clues.concat([response.data]),
                    });
                }
            }
        );
    }

    renderBox() {
        const { isCategory, value } = this.props;
        if (isCategory) {
            return <li>{this.props.category}</li>;
        } else {
            return <li>{value}</li>;
        }
    }

    render() {
        return this.renderBox();
    }
}
