import React, { useState } from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useHistory,
} from 'react-router-dom';
import './App.css';

function App() {
    const [numCategories, setNumCategories] = useState();
    const [numClues, setNumClues] = useState();
    const [createBoard, setCreateBoard] = useState(false);
    let history = useHistory();

    const handleSubmit = () => {
        if (numCategories > 0 && numClues > 0) {
            createBoard(true);
        }
    };
    const handleInputChange = (e) => {
        const input = e.target.name;

        switch (input) {
            case 'categories':
                setNumCategories(parseInt(e.target.value));
                break;
            case 'clues':
                setNumClues(parseInt(e.target.value));
                break;
            default:
                break;
        }
    };
    return (
        <div className="App">
            <input
                type="number"
                placeholder="Number of Categories"
                onChange={handleInputChange}
                name="categories"
            />
            <input
                type="number"
                placeholder="Number of Clues"
                onChange={handleInputChange}
                name="clues"
            />
            <button onClick={handleSubmit}>Create</button>
        </div>
    );
}

export default App;
