import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';
import axios from 'axios';
import Box from '../components/Box';
import { withRouter } from 'react-router-dom';

class Board extends Component {
    static contextType = AppContext;
    state = {
        categories: [],
        clues: [],
        isLoading: true,
        offset: 0,
    };
    componentDidMount() {
        const context = this.context;
        if (context.categories.length > 0 && context.clues.length > 0) {
        } else {
            this.fetchData();
        }
    }

    fetchData = async () => {
        const context = this.context;
        const dailyDouble = Math.floor(Math.random() * context.clueCount);
        context.setDailyDouble(dailyDouble);

        try {
            axios('http://jservice.io/api/categories?count=100').then(
                async (response) => {
                    await response.data.map((item) => {
                        const { clues_count, id } = item;
                        if (
                            clues_count >= context.clueCount &&
                            this.state.categories.length < context.categoryCount
                        ) {
                            this.setState({
                                categories: this.state.categories.concat(item),
                            });
                            context.setCategories(this.state.categories);

                            axios(
                                `http://jservice.io/api/clues?category=${id}`
                            ).then((response) => {
                                this.setState({
                                    clues: this.state.clues.concat([
                                        response.data,
                                    ]),
                                });
                                context.setClues(
                                    this.state.clues.concat([response.data])
                                );

                                if (
                                    this.state.clues.length ===
                                    context.categoryCount
                                ) {
                                    this.setState({
                                        isLoading: false,
                                        clueCount: context.clueCount,
                                    });
                                    context.setLoading(false);
                                }
                            });
                        }
                    });
                }
            );
        } catch (error) {
            console.log(error);
        }
    };

    shouldComponentUpdate() {
        if (this.context.isLoading) {
            return true;
        }
        return false;
    }

    resetBoard() {
        let reset = window.confirm('Are you sure you want to restart?');
        if (reset) {
            props.location.push('/');
        }
    }

    render() {
        const { categories, clueCount, clues } = this.context;

        return (
            <div className="Board">
                {this.context.isLoading ? (
                    <h2>Creating...</h2>
                ) : (
                    <React.Fragment>
                        {categories.map((item, index) => {
                            const { title } = item;
                            if (clues.length > 0) {
                                const clueArray = clues[index];
                                return (
                                    <ul key={`col-${index}`} className="list">
                                        <Box
                                            category={title}
                                            className="category"
                                            isCategory={true}
                                        />
                                        {clueArray.map((key, index) => {
                                            const {
                                                value,
                                                question,
                                                answer,
                                                category,
                                                id,
                                            } = key;
                                            const { title } = category;
                                            const selected = this.context.answeredClues.includes(
                                                id
                                            );

                                            if (index < clueCount) {
                                                return (
                                                    <Box
                                                        id={id}
                                                        value={value}
                                                        question={question}
                                                        answer={answer}
                                                        title={title}
                                                        selected={selected}
                                                    />
                                                );
                                            } else {
                                                return null;
                                            }
                                        })}
                                    </ul>
                                );
                            } else {
                                return null;
                            }
                        })}
                        <button onClick={this.resetBoard}>reset</button>
                    </React.Fragment>
                )}
            </div>
        );
    }
}

export default withRouter(Board);
