import React, { Component } from 'react';
import axios from 'axios';

export default class Box extends Component {
    state = {
        isCategory: false,
    };

    componentDidMount() {
        const { id } = this.props;

        if (!this.state.isCategory) {
            axios(`http://jservice.io/api/clues?category=${id}`).then(
                (response) => {
                    const { value } = response.data[0];
                    console.log(value);
                    // this.setState({
