                    //     }
                    // });
                }
            );
        }
    }

    renderBox() {
        const { isCategory, id } = this.props;
        if (isCategory) {
            return <li>{this.props.category}</li>;
        } else {
            return <li></li>;
        }
    }

    render() {
        return this.renderBox();
    }
}
