const reducer = (state, action) => {
    switch (action.type) {
        case 'reset':
            return 'hhgh';
        case 'setCategories':
            return {
                ...state,
                categories: [action.payload],
            };
        case 'setClues':
            const value = action.payload;
            return {
                ...state,
                clues: [...state.clues, value],
            };
        default:
            return state;
    }
};

export default reducer;
