const initialState = {
    categories: [],
    clues: [],
    validBoard: false,
    offset: 0,
    dailyDouble: 0,
    answeredClues: [],
    isLoading: true,
};

const reducer = (state, action) => {
    switch (action.type) {
        case 'reset':
            return { ...state, initialState };
        case 'setCategories':
            return {
                ...state,
                categories: [...state.categories, action.payload],
            };
        case 'setClues':
            return {
                ...state,
                clues: [...state.clues, action.payload],
            };
        case 'answeredClues':
            return {
                ...state,
                answeredClues: [...state.answeredClues, action.payload],
            };
        case 'isLoading':
            return {
                ...state,
                isLoading: action.payload,
            };
        default:
            return state;
    }
};

export default reducer;
