import React, { Component } from 'react';

export default class Clue extends Component {
    state = {
        question: '',
        answer: '',
        isLoading: true,
    };
    componentDidMount() {
        const { question, answer } = this.props.location.state;
        this.setState({
            question: question,
            answer: answer,
            isLoading: false,
        });
    }

    render() {
        const { question, answer, isLoading } = this.state;
        return (
            <div>
                {isLoading ? (
                    ''
                ) : (
                    <>
                        <p>{question}</p>
                        <button onClick={this.handleClick}>Back</button>
                    </>
                )}
            </div>
        );
    }
}
