import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';
import axios from 'axios';

export default class Box extends Component {
    static contextType = AppContext;
    state = {
        isCategory: false,
    };

    componentDidMount() {
        const { id } = this.props;
        if (id) {
            axios(`http://jservice.io/api/clues?category=${id}`).then(
                (response) => {
                    console.log(response.data);
                    this.setState({
                        clues: response.data,
                    });
                }
            );
        }
    }

    renderBox() {
        const { isCategory } = this.props;
        const { clue } = this.state || {};
        const context = this.context;
        const { value } = clue;

        if (isCategory) {
            return <li>{this.props.category}</li>;
        } else {
            return Array(context.clueCount).fill(this.state);
        }
    }

    render() {
        return this.renderBox();
    }
}
