import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';
import { withRouter } from 'react-router-dom';

class Box extends Component {
    static contextType = AppContext;
    state = {
        isCategory: false,
    };

    componentDidMount() {
        const { question, id, answer, title, selected } = this.props;
        this.setState({
            question: question,
            answer: answer,
            title: title,
            id: id,
            selected: selected,
        });
    }

    handleClick = (e) => {
        const context = this.context;
        const { question, id, title } = this.state;
        context.setAnsweredClues(context.answeredClues.concat(id));
        this.props.history.push({
            pathname: `/clue/${id}`,
            state: { question: question, clue: title },
        });
    };

    render() {
        const { isCategory, category, value } = this.props;
        const { selected } = this.state;
        if (isCategory) {
            return <li className="category">{category}</li>;
        } else {
            return (
                <li className="clue" onClick={this.handleClick}>
                    {selected ? '' : value}
                </li>
            );
        }
    }
}

export default withRouter(Box);
