    componentDidMount() {
        let value = this.context;
        try {
            axios('http://jservice.io/api/categories?count=100').then(
                async (response) => {
                    await response.data.map((item) => {
                        const { clues_count } = item;
                        if (clues_count >= value.categoryCount) {
                            value.setCategories(
                                value.categories.concat([item])
                            );
                        }
                    });
                }
            );
        } catch (error) {
            console.log(error);
        }
    }

    render() {
        return <div>this is a board</div>;
    }
}
