import React, { Component } from 'react';

export default class Clue extends Component {
    state = {
        question: '',
        answer: '',
        isLoading: true,
        showClue: true,
    };
    componentDidMount() {
        const { question, answer, clue } = this.props.location.state;
        this.setState({
            question: question,
            answer: answer,
            isLoading: false,
            clue: clue,
        });
    }

    toggleClue = () => {
        this.setState({ showClue: !this.state.showClue });
    };

    handleClick = () => {
        this.props.history.goBack();
    };

    render() {
        const { question, showClue, clue } = this.state;
        return (
            <div>
                {!showClue ? <p>{clue}</p> : <p>{question}</p>}
                {!showClue ? (
                    <button onClick={this.toggleClue}>Show Question</button>
                ) : (
                    <button onClick={this.toggleClue}>Show Clue</button>
                )}
                <button onClick={this.handleClick}>Done</button>
            </div>
        );
    }
}
