import React, { Component } from 'react';
import axios from 'axios';

export default class Box extends Component {
    state = {
        isCategory: false,
    };

    componentDidMount() {
        const { id } = this.props;

        axios(`http://jservice.io/api/clues?category=${id}`).then(
            (response) => {
                console.log(response.data);
                // this.setState({
                //     clue: {
                //         value: response.data.value
                //     }
                // });
            }
        );
    }

    renderBox() {
        const { isCategory, id } = this.props;
        if (isCategory) {
            return <li>{this.props.category}</li>;
        } else {
            return <li>{id}</li>;
        }
    }

    render() {
        return this.renderBox();
    }
}
