import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';
import axios from 'axios';
import Box from '../components/Box';

export default class Board extends Component {
    static contextType = AppContext;
    state = {
        categories: [],
        clues: [],
        isLoading: true,
        offset: 0,
    };
    componentDidMount() {
        const context = this.context;
        if (context.categories.length > 0 && context.clues.length > 0) {
        } else {
            this.fetchData();
        }
    }

    dailyDouble = () => {
        const value = this.context;
        const randNum = Math.random() * value.clueCount;

        return randNum;
    };

    fetchData = async () => {
        const value = this.context;
        const dailyDouble = Array(2).fill(Math.floor(this.dailyDouble()));
        console.log(dailyDouble);
        try {
            axios('http://jservice.io/api/categories?count=100').then(
                async (response) => {
                    await response.data.map((item) => {
                        const { clues_count, id } = item;
                        if (
                            clues_count >= value.clueCount &&
                            this.state.categories.length < value.categoryCount
                        ) {
                            this.setState({
                                categories: this.state.categories.concat(item),
                            });

                            axios(
                                `http://jservice.io/api/clues?category=${id}`
                            ).then((response) => {
                                this.setState({
                                    clues: this.state.clues.concat([
                                        response.data,
                                    ]),
                                });
                                if (
                                    this.state.clues.length ===
                                    value.categoryCount
                                ) {
                                    this.setState({
                                        isLoading: false,
                                        clueCount: value.clueCount,
                                    });
                                }
                            });
                        }
                    });
                }
            );
        } catch (error) {
            console.log(error);
        }
    };

    shouldComponentUpdate() {
        if (this.state.isLoading) {
            return true;
        }
        return false;
    }

    render() {
        const { categories, clues, offset, clueCount } = this.state;

        return (
            <div className="Board">
                {this.state.isLoading ? (
                    <h2>Creating...</h2>
                ) : (
                    <React.Fragment>
                        {categories.map((item, index) => {
                            const { title, id } = item;
                            if (clues.length > 0) {
                                const clueArray = clues[index];
                                return (
                                    <ul key={`col-${index}`} className="list">
                                        <Box
                                            category={title}
                                            className="category"
                                            isCategory={true}
                                        />
                                        {clueArray.map((key, index) => {
                                            const {
                                                value,
                                                question,
                                                answer,
                                                category,
                                            } = key;
                                            const { title } = category;

                                            if (index < clueCount) {
                                                return (
                                                    <Box
                                                        id={id}
                                                        value={value}
                                                        question={question}
                                                        answer={answer}
                                                        title={title}
                                                    />
                                                );
                                            } else {
                                                return null;
                                            }
                                        })}
                                    </ul>
                                );
                            } else {
                                return null;
                            }
                        })}
                    </React.Fragment>
                )}
            </div>
        );
    }
}
