const reducer = (state, action) => {
    switch (action.type) {
        case 'reset':
            return 'hhgh';
        case 'setCategories':
            return {
                ...state,
                categories: [...state.categories, action.payload],
            };
        case 'setClues':
            console.log(state, action);
            return {
                ...state,
                clues: [...state.clues, [action.payload]],
            };
        default:
            return state;
    }
};

export default reducer;
