import React, { createContext, useReducer, useState } from 'react';
import AppContext from './appContext';

const initialState = {
    categories: [],
    clues: [],
    validBoard: false,
    offset: 0,
    dailyDouble: 0,
    answeredClues: [],
    isLoading: true,
};
const AppProvider = () => {
    return <AppContext.Provider value={{ initialState }}></AppContext.Provider>;
};

