import React, { Component, useContext, useEffect, useState } from 'react';
import AppContext from '../context/appContext';

const Clue = (props) => {
    const { question, clue } = props.location.state;
    const { toggleClue, setToggleClue } = useState(false);

    useEffect(() => {}, []);

    const toggleButton = () => {
        setToggleClue(true);
    };

    const handleClick = () => {
        this.props.history.goBack();
    };

    return (
        <div>
            {!toggleClue ? <p>{clue}</p> : <p>{question}</p>}
            {!toggleClue ? (
                <button onClick={toggleButton}>Show Question</button>
            ) : (
                <button onClick={toggleButton}>Show Clue</button>
            )}
            <button onClick={handleClick}>Done</button>
        </div>
    );
};

export default Clue;
