import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
    const [numCategories, setNumCategories] = useState();
    const [numClues, setNumClues] = useState();
    const handleSubmit = () => {};
    const handleInputChange = (e) => {
        const input = e.target.name;

        switch (input) {
            case 'categories':
                setNumCategories(e.target.value);
                break;
            case 'clues':
                setNumClues(e.target.value);
                break;
            default:
                break;
        }
    };
    return (
        <div className="App">
            <input
                type="number"
                placeholder="Number of Categories"
                onChange={handleInputChange}
                name="categories"
            />
            <input
                type="number"
                placeholder="Number of Clues"
                onChange={handleInputChange}
                name="clues"
            />
            <button onClick={handleSubmit}>Create</button>
        </div>
    );
}

export default App;
