        setClueState({ showClue: !clueState.showClue });
    };

    const handleClick = () => {
        this.props.history.goBack();
    };

    return (
        <div>
            {!showClue ? <p>{}</p> : <p>{question}</p>}
            {!showClue ? (
                <button onClick={toggleClue}>Show Question</button>
            ) : (
                <button onClick={toggleClue}>Show Clue</button>
            )}
            <button onClick={handleClick}>Done</button>
        </div>
    );
};

export default Clue;
