import React, { useContext, useEffect, useState } from 'react';
import AppContext from '../context/appContext';
import { withRouter } from 'react-router-dom';

const Box = (props) => {
    const { state, dispatch } = useContext(AppContext);
    const [clue, setClue] = useState();
    const { isCategory, category, value } = props;

    useEffect(() => {
        const { question, id, answer, title } = props;
        const state = {
            question: question,
            answer: answer,
            title: title,
            id: id,
        };

        setClue(state);
    }, []);

    const handleClick = (e) => {
        const { question, id, title } = clue;
        dispatch({ type: 'answeredClues', payload: id });
        props.location.push({
            pathname: `/clue/${id}`,
            state: { question: question, clue: title },
        });
    };

    if (isCategory) {
        return <li className="category">{category}</li>;
    } else {
        return (
            <li className="clue" onClick={handleClick}>
                {value}
            </li>
        );
    }
};

export default withRouter(Box);
