import React, { Component } from 'react';
import { AppContext } from '../providers/AppContext';
import axios from 'axios';
import Box from '../components/Box';

export default class Board extends Component {
    static contextType = AppContext;
    state = {
        categories: [],
        clues: [this.context.clueCount],
        isLoading: true,
    };
    componentDidMount() {
        let value = this.context;
        try {
            axios('http://jservice.io/api/categories?count=100').then(
                async (response) => {
                    await response.data.map((item) => {
                        const { clues_count, id } = item;
                        if (
                            clues_count >= value.categoryCount &&
                            this.state.categories.length < value.categoryCount
                        ) {
                            this.setState({
                                categories: this.state.categories.concat(item),
                            });

                            axios(
                                `http://jservice.io/api/clues?category=${id}`
                            ).then((response) => {
                                this.setState({
                                    clues: this.state.clues.concat([
                                        response.data,
                                    ]),
                                });
                            });
                        }
                    });
                    this.setState({ isLoading: false });
                }
            );
        } catch (error) {
            console.log(error);
        }
    }

    render() {
        const { categories, clues } = this.state;
        return (
            <div className="Board">
                {this.state.isLoading ? (
                    <h2>Loading...</h2>
                ) : (
                    <React.Fragment>
                        {categories.map((item, index) => {
                            const { title } = item;

                            return (
                                <ul key={`col-${index}`}>
                                    <Box
                                        category={title}
                                        className="category"
                                        isCategory={true}
                                    />
                                    {clues.map((item, index) => {
                                        return item.map((clue) => {
                                            const { value } = clue;

                                            return <Box value={value} />;
                                        });
                                    })}
                                </ul>
                            );
                        })}
                    </React.Fragment>
                )}
            </div>
        );
    }
}
