import React, { Component } from 'react';
import axios from 'axios';
import { AppContext } from '../providers/AppContext';

export default class Box extends Component {
    static contextType = AppContext;
    state = {
        isCategory: false,
    };

    componentDidMount() {
        const { id } = this.props;
        const context = this.context;
        console.log(context.offset);

        if (!this.state.isCategory) {
            axios(`http://jservice.io/api/clues?category=${id}`).then(
                (response) => {
                    const { value } = response.data[0];
                    console.log(value);
                    this.setState({
                        value: value,
                    });
                }
            );
        }
        context.setOffset(context.offset++);
    }

    renderBox() {
        const { isCategory, id } = this.props;
        const { value } = this.state;
        if (isCategory) {
            return <li>{this.props.category}</li>;
        } else {
            return <li>{value}</li>;
        }
    }

    render() {
        return this.renderBox();
    }
}
