import React, { createContext, useContext, useReducer } from 'react';

const initialState = {
    categories: [],
    clues: [],
    validBoard: false,
    offset: 0,
    dailyDouble: 0,
    answeredClues: [],
    isLoading: true,
};

const GlobalState = createContext(initialState);

const reducer = (state, action) => {
    switch (action.type) {
        case 'reset':
            return initialState;
        case 'categories':
            return action.payload;
        default:
            return state;
    }
};
export const AppProvider = (children) => {
    const [state, dispatch] = useReducer(reducer, initialState);

    // const [clueCount, setClueCount] = useState();
    // const [categoryCount, setCategoryCount] = useState([]);
    // const [categories, setCategories] = useState([]);
    // const [clues, setClues] = useState([]);
    // const [validBoard, setValidBoard] = useState(false);
    // const [offset, setOffset] = useState(0);
    // const [dailyDouble, setDailyDouble] = useState();
    // const [answeredClues, setAnsweredClues] = useState([]);
    // const [isLoading, setLoading] = useState(true);
    // const [initialState, setInitialState] = useState();

    return (
        <GlobalState.Provider value={{ state, dispatch }}>
            {children}
        </GlobalState.Provider>
    );
};

export const useBoard = () => useContext(GlobalState);
